import Spinner from '/AI_eye_components/Spinner'
import Images from './AI_eye_components/Images'
import Buttons from './AI_eye_components/Buttons'

import { API_URL } from './config'
import './style.css'

export default class Al_eye extends Component {

	state = {

		uploading: false,
		images: []
	}

	onChange = e => {

		const files = Array.from(e.target.files)
		this.setState({uploading: true})

		const formData = new formData()

		files.forEach((file, i) => {formData.append(i.file)})

		fetch(`${API_URL}/image-upload`, {

			method: 'POST',
			body: formData
		})

		.then(res =>res.json())
		.then(images => {

			this.setState({
				uploading: false,
				images
			})
		})

	}

	removeImage = id => {

		this.setState({

			images: this.state.images.filter(image => image.public_id !== id)
		})
	}

